let ProductByCategoryComponent = class ProductByCategoryComponent {
  constructor(productService, cartService, route) {
    this.productService = productService;
    this.cartService = cartService;
    this.route = route;
    this.products = [];
    this.page = {
      size: 0,
      totalElements: 0,
      totalPages: 0,
      number: 0
    };
    this.categoryId = +this.route.snapshot.paramMap.get('id');
    this.currentPage = 1;
    this.pageSize = 1;
