      this.products = response.products;
      this.page = response.page;
    });
  }

  onPageChange(page) {
    this.currentPage = page;
    this.getProductsByCategory();
  }

  onCategoryClick(categoryId) {
    this.categoryId = categoryId;
    this.getProductsByCategory();
  }

