import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { CartItem } from 'src/app/cart-item';
let ProductListComponent = class ProductListComponent {
  constructor(productService, cartService, route, router, userAuth) {
    this.productService = productService;
    this.cartService = cartService;
    this.route = route;
    this.router = router;
    this.userAuth = userAuth;
    this.products = [];
    this.page = {
      size: 0,
      totalElements: 0,
      totalPages: 0,
      number: 0
    };
    this.currentPage = 0;
    this.pageSize = 5;
  }

  ngOnInit() {
    // this.products = this.productService.getAllProducts();
    this.getAllProducts();
  }

  getAllProducts() {
    this.productService.getAllProducts(this.currentPage, this.pageSize).subscribe(response => {
      this.products = response.products;
      this.page = response.page;
    });
  }

  onPageChange(pageNumber) {
    this.currentPage = pageNumber;
    this.getAllProducts();
  }

  onItemsPerPageChange(pageSize) {
    this.pageSize = pageSize;
    this.getAllProducts();
  }

  addToCart(theProduct) {
    console.log(`Adding to cart: ${theProduct.name}, ${theProduct.unitPrice}`);
    let theCartItem = new CartItem(theProduct.id, theProduct.name, theProduct.imageUrl, theProduct.unitPrice // theProduct.discount
    );
    this.cartService.addToCart(theCartItem);
  }

  redirectToViewDetails(id) {
    this.router.navigate(['/products', id]);
  }

};
